mapper.update:
update like_bookmark set
ltype='1', ldate = to_char(sysdate, 'yy/mm/dd hh24:mi:ss') where
ucode=#{ucode} and bcode=#{bcode}

update like_bookmark set
ltype='', ldate = '' where ucode=#{ucode} and
bcode=#{bcode}

mapper.select:
select bbtype
from like_bookmark
where ucode = #{ucode} and bcode=#{bcode}
